INSERT INTO articles (id, title, slug, excerpt, content, section_id, published_at, updated_at, is_pinned, created_at) VALUES
  (1, 'مصنوعی ذہانت کی جدید ترقی', 'modern-advancements-in-artificial-intelligence', 'مصنوعی ذہانت کے میدان میں حالیہ پیش رفت اور اس کے ممکنہ اثرات کا جائزہ', '# مصنوعی ذہانت کی جدید ترقی\n\nمصنوعی ذہانت نے پچھلی دہائی میں تیزی سے ترقی کی ہے اور یہ ہماری روزمرہ زندگی کا اہم حصہ بن چکی ہے۔\n\n> آج کل کی مصنوعی ذہانت صرف ایک ٹول نہیں بلکہ ایک مددگار شریک ہے\n\n## اہم پیش رفت\n\n- مشین لرننگ کے نئے طریقے\n- ڈیپ لرننگ میں بہتری\n- قدرتی زبان کی پروسیسنگ\n\nمصنوعی ذہانت کی مدد سے ہم بڑے پیمانے پر ڈیٹا کا تجزیہ کر سکتے ہیں اور اہم فیصلے آسانی سے لے سکتے ہیں۔\n\n## مستقبل کی امیدیں\n\nآنے والے سالوں میں مصنوعی ذہانت مزید ترقی کرے گی اور ہمیں بہترین نتائج فراہم کرے گی۔', 1, '2025-12-15', '2025-12-15', 1, '2025-12-15'),
  (2, 'کوانٹم کمپیوٹنگ کی بنیادی باتیں', 'basics-of-quantum-computing', 'کوانٹم کمپیوٹنگ کیا ہے اور یہ روایتی کمپیوٹنگ سے کیسے مختلف ہے', '# کوانٹم کمپیوٹنگ کی بنیادی باتیں\n\nکوانٹم کمپیوٹنگ ایک انقلابی ٹیکنالوجی ہے جو کوانٹم میکانیات کے اصولوں پر کام کرتی ہے۔\n\n## روایتی اور کوانٹم کمپیوٹنگ میں فرق\n\n> کوانٹم کمپیوٹر ہزاروں حسابات ایک ساتھ کر سکتے ہیں\n\n### کلیدی خصوصیات:\n\n- سوپر پوزیشن\n- انتینگلمنٹ\n- انٹرفیرنس\n\nیہ ٹیکنالوجی کرپٹوگرافی، دوائیوں کی دریافت، اور مالیاتی ماڈلنگ میں انقلاب برپا کرے گی۔', 1, '2025-12-10', '2025-12-10', 0, '2025-12-10'),
  (3, '5G ٹیکنالوجی اور مستقبل', '5g-technology-and-future', '5G کی آمد کے ساتھ ٹیکنالوجی میں آنے والی تبدیلیاں', '# 5G ٹیکنالوجی اور مستقبل\n\n5G نے ہمیں تیز رفتار انٹرنیٹ اور بہتر کنیکٹویٹی فراہم کی ہے۔\n\n## 5G کی خصوصیات\n\n- 10 گیگابٹ پر سیکنڈ تک رفتار\n- 1 ملی سیکنڈ تک لیٹنسی\n- ملین ڈیوائسز کا کنیکشن\n\n> 5G صرف تیز رفتار نہیں بلکہ نئی قسم کی کنیکٹویٹی ہے\n\n### اثرات:\n\n- آٹومیٹڈ گاڑیاں\n- انٹرنیٹ آف تھنگز\n- ویرچوئل ریئلٹی\n\nیہ ٹیکنالوجی سماج اور معیشت پر گہرا اثر ڈالے گی۔', 1, '2025-12-05', '2025-12-05', 0, '2025-12-05'),
  (4, 'بلاک چین ٹیکنالوجی کی وضاحت', 'blockchain-technology-explained', 'بلاک چین کیسے کام کرتا ہے اور اس کی اہمیت کیا ہے', '# بلاک چین ٹیکنالوجی کی وضاحت\n\nبلاک چین ایک منقسم اور غیر مرکزی لیجر سسٹم ہے جو ڈیٹا کو محفوظ طریقے سے اسٹور کرتا ہے۔\n\n## کام کا طریقہ\n\nبلاک چین میں ہر ٹرانزیکشن کو ایک بلاک میں محفوظ کیا جاتا ہے جو پچھلے بلاک سے منسلک ہوتا ہے۔\n\n> ہر بلاک میں ہش کرپٹوگرافک فنکشن کے ذریعے محفوظ ہوتا ہے\n\n### اہم فوائد:\n\n- شفافیت\n- سیکیورٹی\n- غیر مرکزی کنٹرول\n\nاس ٹیکنالوجی کا استعمال کرنسی، سپلائی چین، اور ووٹنگ سسٹم میں کیا جا رہا ہے۔', 1, '2025-11-28', '2025-11-28', 0, '2025-11-28'),
  (5, 'روبوٹکس میں نئی پیش رفت', 'new-advancements-in-robotics', 'روبوٹکس کی ترقی اور اس کے ممکنہ استعمال', '# روبوٹکس میں نئی پیش رفت\n\nروبوٹکس کی دنیا میں مسلسل نئی ترقی ہو رہی ہے جو انسانوں کی زندگی آسان بنا رہی ہے۔\n\n## جدید روبوٹس کی خصوصیات\n\nجدید روبوٹس میں اے آئی کا استعمال کر کے وہ پیچیدہ کام انجام دے سکتے ہیں۔\n\n> روبوٹس اب صرف فیکٹریوں میں نہیں بلکہ گھروں میں بھی کام کرتے ہیں\n\n### استعمال کے شعبے:\n\n- طبی امداد\n- تعلیم\n- کھیل\n- گھریلو کام\n\nآنے والے سالوں میں روبوٹس ہمارے معاشرے کا لازمی حصہ بن جائیں گے۔', 1, '2025-11-20', '2025-11-20', 0, '2025-11-20');
INSERT INTO articles (id, title, slug, excerpt, content, section_id, published_at, updated_at, is_pinned, created_at) VALUES
  (6, 'دل کی صحت کے لیے 10 تجاویز', '10-tips-for-heart-health', 'دل کی بیماریوں سے بچنے کے آسان اور مؤثر طریقے', '# دل کی صحت کے لیے 10 تجاویز\n\nصحت مند دل کے لیے صحیح طرز زندگی اختیار کرنا بہت اہم ہے۔\n\n> صحت مند دل کے لیے متوازن غذا اور ورزش دونوں ضروری ہیں\n\n## بنیادی تجاویز:\n\n### 1. باقاعدگی سے ورزش کریں\nہفتے میں کم از کم 150 منٹ ایروبک ورزش کریں\n\n### 2. صحت مند غذا کھائیں\n- پھل اور سبزیاں\n- سمندری غذا\n- مکئی کا آٹا\n\n### 3. تمباکو نہ کھائیں\nتمباکو نوشی دل کی بیماریوں کا اہم سبب ہے\n\n### 4. تناؤ کو کم کریں\nیوگا اور مراقبہ دل کی صحت کے لیے بہترین ہیں\n\n### 5. مناسب نیند لیں\nدن میں 7-9 گھنٹے کی نیند ضروری ہے', 2, '2025-12-12', '2025-12-12', 1, '2025-12-12'),
  (7, 'کیا کریں جب بخار ہو', 'what-to-do-when-you-have-fever', 'بخار کے دوران احتیاطی تدابیر اور بہترین علاج', '# کیا کریں جب بخار ہو\n\nبخار ایک عام علامت ہے جو جسم کے قدرتی دفاعی نظام کی نشاندہی ہے۔\n\n## فوری اقدامات\n\nجب آپ کو بخار ہو تو درج ذیل اقدامات کریں:\n\n> بخار کے دوران جسم میں پانی کی کمی کا خدشہ ہوتا ہے\n\n### اہم کام:\n\n- آرام کریں\n- پانی کی زیادہ مقدار پیئں\n- ہلکی غذا کھائیں\n\n### کب ڈاکٹر سے رابطہ کریں:\n\n- بخار 103°F سے زیادہ ہو\n- 3 دن سے زیادہ جاری رہے\n- سانس لینے میں دشواری ہو\n\nگھریلو علاج کے ساتھ ساتھ ضروری ہے کہ آپ اپنے ڈاکٹر سے مشورہ کریں۔', 2, '2025-12-08', '2025-12-08', 0, '2025-12-08'),
  (8, 'وٹامن ڈی کی اہمیت', 'importance-of-vitamin-d', 'وٹامن ڈی کی کمی اور اس کے نتائج، بہترین حل', '# وٹامن ڈی کی اہمیت\n\nوٹامن ڈی ایک اہم وٹامن ہے جو ہڈیوں اور دانتوں کے لیے ضروری ہے۔\n\n## وٹامن ڈی کے فوائد\n\n> سورج کی روشنی ہمارے جسم کو وٹامن ڈی بنانے میں مدد کرتی ہے\n\n### اہم فوائد:\n\n- ہڈیوں کی مضبوطی\n- دانت کی صحت\n- قوت مدافعت بہتر بنانا\n- دل کی بیماری سے تحفظ\n\n### کمی کی علامات:\n\n- تھکاوٹ\n- پیٹ کا درد\n- پیرڈیتکس\n- پٹھوں کا کمزور ہونا\n\nدن میں 15-20 منٹ سورج کی روشنی وٹامن ڈی کی ضرورت پوری کرنے کے لیے کافی ہے۔', 2, '2025-12-03', '2025-12-03', 0, '2025-12-03'),
  (9, 'نیند کے فوائد اور مسائل', 'benefits-of-sleep-and-disorders', 'صحت مند نیند کی اہمیت اور نیند کی خرابی کے مسائل', '# نیند کے فوائد اور مسائل\n\nصحت مند نیند ہماری جسمانی اور ذہنی صحت کے لیے بہت اہم ہے۔\n\n## صحت مند نیند کے فوائد\n\n> نیند کے دوران ہمارا دماغ معلومات کو پروسیس کرتا ہے\n\n### اہم فوائد:\n\n- یادداشت بہتر بنانا\n- قوت مدافعت مضبوط بنانا\n- تناؤ کم کرنا\n- وزن کنٹرول\n\n### نیند کی مطلوبہ مدت:\n\n- بالغ افراد: 7-9 گھنٹے\n- بچے: 8-12 گھنٹے\n- بزرگ: 7-8 گھنٹے\n\n### نیند کی خرابی کی علامات:\n\n- صبح اٹھنے پر تھکاوٹ\n- دن کے وقت نیند آنا\n- آسانی سے جاگنا\n\nاگر نیند کی پریشانی ہو تو ماہر سے رابطہ کریں۔', 2, '2025-11-25', '2025-11-25', 0, '2025-11-25'),
  (10, 'ذیابطیس کی روک تھام', 'prevention-of-diabetes', 'ذیابطیس کی مختلف اقسام اور ان سے بچاؤ کے طریقے', '# ذیابطیس کی روک تھام\n\nذیابطیس ایک عادت کی بیماری ہے جو خون میں شکر کی زیادہ مقدار سے ہوتی ہے۔\n\n## ذیابطیس کی اقسام\n\n> ذیابطیس کی اکثر قسمیں طرز زندگی میں تبدیلی سے کنٹرول کی جا سکتی ہیں\n\n### اہم اقسام:\n\n#### ٹائپ 1 ذیابطیس\n- جسم انسولین نہیں بناتا\n- بچپن میں شروع ہوتی ہے\n\n#### ٹائپ 2 ذیابطیس\n- انسولین کی کمی یا غیر مؤثر استعمال\n- عام ترین قسم\n\n### روک تھام کے طریقے:\n\n- صحت مند وزن برقرار رکھیں\n- باقاعدگی سے ورزش کریں\n- متوازن غذا کھائیں\n- کھانے کا اوقات منظم رکھیں\n- تناؤ کم کریں\n\nمنظم طرز زندگی سے آپ ذیابطیس سے بچ سکتے ہیں۔', 2, '2025-11-15', '2025-11-15', 0, '2025-11-15');
INSERT INTO articles (id, title, slug, excerpt, content, section_id, published_at, updated_at, is_pinned, created_at) VALUES
  (11, 'بچوں میں دلچسپی کی تربیت', 'training-children-interests', 'بچوں میں دلچسپی پیدا کرنے اور اسے فروغ دینے کے طریقے', '# بچوں میں دلچسپی کی تربیت\n\nبچوں میں دلچسپی پیدا کرنا والدین اور اساتذہ کی ذمہ داری ہے۔\n\n## اہم اصول\n\n> ہر بچہ منفرد ہے اور اس کی اپنی دلچسپیاں ہیں\n\n### بنیادی طریقے:\n\n- بچے کی بات سنیں\n- مختلف سرگرمیاں آزمائیں\n- مثبت حوصلہ افزائی کریں\n- مشکل کو آسان بنائیں\n\n### دلچسپی کے میدان:\n\n- کھیل\n- آرٹ\n- موسیقی\n- سائنس\n- کھیل\n\n### والدین کا کردار:\n\n- رہنمائی کریں لیکن مجبور نہ کریں\n- کامیابی اور ناکامی دونوں سیکھنے کا حصہ ہے\n- بچے کو وقت اور موقع فراہم کریں\n\nصبر اور محبت کے ساتھ آپ بچوں میں مضبوط دلچسپی پیدا کر سکتے ہیں۔', 3, '2025-12-14', '2025-12-14', 1, '2025-12-14'),
  (12, 'اون لائن تعلیم کے فوائد', 'benefits-of-online-education', 'این لائن تعلیم کے فوائد اور چیلنجز', '# اون لائن تعلیم کے فوائد\n\nاین لائن تعلیم نے تعلیم کی دنیا میں انقلاب برپا کر دیا ہے۔\n\n## اہم فوائد\n\n> این لائن تعلیم جغرافیائی حدود کو ختم کر دیتا ہے\n\n### بنیادی فوائد:\n\n- گھر سے تعلیم\n- مختلف کورسز کا انتخاب\n- اپنی رفتار سے سیکھنا\n- کم خرچ\n- ریکارڈ شدہ لیکچرز\n\n### چیلنجز:\n\n- کم توجہ\n- تکنیکی مسائل\n- کم تعلیمی تعامل\n- سرٹیفکیشن کا مسئلہ\n\n### کامیابی کے طریقے:\n\n- منظم ماحول بنائیں\n- تعلیمی گروپس بنائیں\n- باقاعدگی برقرار رکھیں\n- ٹیچر سے رابطہ رکھیں\n\nاین لائن تعلیم مستقبل کی تعلیم کا حصہ ہے۔', 3, '2025-12-09', '2025-12-09', 0, '2025-12-09'),
  (13, 'یادداشت بہتر بنانے کے طریقے', 'ways-to-improve-memory', 'یادداشت بہتر بنانے کے ثابت شدہ اور مؤثر طریقے', '# یادداشت بہتر بنانے کے طریقے\n\nمضبوط یادداشت تعلیم اور کامیابی کے لیے بہت اہم ہے۔\n\n## ثابت شدہ طریقے\n\n> یادداشت ورزش کرنے سے بہتر ہوتی ہے جیسے پٹھے\n\n### مؤثر طریقے:\n\n#### 1. مکرر کرنا\nایک ہی معلومات کو بار بار دہرائیں\n\n#### 2. ارتباط بنانا\nنئی معلومات کو پرانی سے جوڑیں\n\n#### 3. بصیری طریقہ\nتصویری حافظہ استعمال کریں\n\n#### 4. سنینا\nآواز میں دہرائیں\n\n### دیگر مددگار طریقے:\n\n- صحت مند نیند\n- ورزش\n- یوگا\n- صحت مند غذا\n- تناؤ کم کرنا\n\n### غذا میں شامل کریں:\n\n- نٹس\n- انگور\n- سبز پھل\n- مچھلی\n- انڈے\n\nصبر اور مستقل مزاجی کے ساتھ آپ یادداشت بہتر بنا سکتے ہیں۔', 3, '2025-12-04', '2025-12-04', 0, '2025-12-04'),
  (14, 'مذاکرات کی ہنر', 'art-of-negotiation', 'مذاکرات کی بنیادی اصول اور کامیاب مذاکرات کے راز', '# مذاکرات کی ہنر\n\nمذاکرات زندگی کے ہر شعبے میں اہم مہارت ہے۔\n\n## بنیادی اصول\n\n> کامیاب مذاکرہ دونوں فریقوں کے لیے فائدہ مند ہونا چاہیے\n\n### اہم اصول:\n\n#### 1. تیاری\nمذاکرت سے پہلے مکمل تیاری کریں\n\n#### 2. سننا\nدوسرے کی بات غور سے سنیں\n\n#### 3. سوچنا\nجواب دینے سے پہلے سوچیں\n\n#### 4. صبر\nعجلت نہ کریں\n\n### مفید تجاویز:\n\n- آداب سے بات کریں\n- جذبات کو کنٹرول کریں\n- مختلف آپشنز پر غور کریں\n- نرم لہجہ استعمال کریں\n- مفاہمت کی کوشش کریں\n\n### عام غلطیاں:\n\n- جلدی فیصلہ کرنا\n- دوسرے کی بات نہ سننا\n- لڑائی کرنا\n- متبادل طریقے نہ دیکھنا\n\nمذاکرات کی مہارت مشق سے بہتر ہوتی ہے۔', 3, '2025-11-30', '2025-11-30', 0, '2025-11-30'),
  (15, 'پیداواری صلاحیت بڑھانا', 'increasing-productivity', 'کام کی پیداواری صلاحیت بڑھانے کے مؤثر طریقے', '# پیداواری صلاحیت بڑھانا\n\nزیادہ کام کم وقت میں کرنا ہر شخص کا ہدف ہے۔\n\n## بنیادی حکمت عملی\n\n> مؤثر منصوبہ بندی پیداواری صلاحیت کا کلیدی عنصر ہے\n\n### اہم حکمت عملیاں:\n\n#### 1. وقت کی منصوبہ بندی\nدن کا شیڈول بنائیں\n\n#### 2. ترجیح\nمہم کاموں کو پہلے کریں\n\n#### 3. توجہ\nایک وقت میں ایک کام\n\n#### 4. آرام\nبریکس لینا ضروری ہے\n\n### مؤثر طریقے:\n\n- وقت کی حد مقرر کریں\n- ڈیسٹریکشنز کم کریں\n- ٹولز کا استعمال کریں\n- ٹیم کے ساتھ کام کریں\n- کام مکمل ہونے پر انعام لیں\n\n### عام مسائل:\n\n- سوشل میڈیا\n- غیر ضروری میٹنگز\n- منصوبہ بندی کی کمی\n- بریک نہ لینا\n\nمستقل مزاجی اور مشق سے آپ اپنی پیداواری صلاحیت بہتر بنا سکتے ہیں۔', 3, '2025-11-22', '2025-11-22', 0, '2025-11-22');
